type ProtoObject<T> = Omit<T, "toJSON">;

interface IProto {
	toJSON?(): ProtoObject<this>;
}

interface IResponse extends IProto {
	error?: any;
}

interface IAmuletBadgeData extends IProto {
	/** 表内的印章id */
	id: number;
	/** 实例id */
	uid: number;
	store: string[];
	/** 随机值，用于印章随机角度展示等，范围 1-100000 */
	random: number;
}

interface IAmuletEffectData extends IProto {
	/** 表内的护身符id */
	id: number;
	/** 实例id */
	uid: number;
	store?: string[];
	/** 印章数据，可能为空 */
	badge?: IAmuletBadgeData;
	/** （客户端数据）护身符占用体积 */
	volume?: number;
	/** 护身符标记位，tags[0] = 当前护身符是否回合结束消失 */
	tags?: number[];
}

interface IAmuletTile extends IProto {
	id: number;
	tile: string;
}

interface IAmuletBuffData extends IProto {
	/** 表内的buff id */
	id: number;
	store: string[];
}

interface IAmuletGameShopGoods extends IProto {
	id: number;
	sold: boolean;
	goods_id: number;
	price: number;
}

interface IAmuletActivityTingInfo extends IProto {
	/** 打这张牌能听，在换牌阶段没有这个字段 */
	tile: string;
	fan: string;
	/** 听的牌 */
	ting_tile: string;
}

interface IAmuletShowDesktopTileData extends IProto {
	id: number;
	/** 位置，从后往前数第几个，最小为0，发牌后不会随着摸牌而变化 */
	pos: number;
}

interface IAmuletMingInfo extends IProto {
	type: number;
	tile_list: number[];
}

interface IAmuletGameOperation extends IProto {
	/** 1-切牌 4-杠 8-和 99-模切，只能打刚刚摸到的牌 100-开始游戏(仅限换牌阶段) 101-换手牌 */
	type: number;
	/** type = 4 时可以杠的列表 */
	gang: IAmuletGameOperation_GangTiles[];
	/** 换手牌时表示最多可以换多少张牌 */
	value: number;
}

interface IAmuletGameOperation_GangTiles extends IProto {
	tiles: number[];
}

interface IAmuletTileScore extends IProto {
	tile: string;
	score: string;
}

interface IActivityAmuletHuRecord extends IProto {
	/** 得分 */
	point: string;
	/** 牌型  例：1m2m3m4m4m,5p5p5p0p,7s7s7s7s */
	pai: string;
	/** 番数 */
	fan: string;
	/** 分 */
	base: string;
	effect_builds: IActivityAmuletEffectRecordData[];
}

interface IAmuletEffectCounterData extends IProto {
	effect_id: number;
	pack_candidate_count: number;
	gain_count: number;
}

interface IAmuletGameRecordData extends IProto {
	yiman_count: number;
	level_hu_count: number;
	game_hu_count: number;
	effect_gain: number;
	coin_consume: string;
	coin_gain: string;
	highest_hu: IActivityAmuletHuRecord;
	highest_level_score: string;
	highest_fan: string;
	pack_count: number;
	round_count: number;
	/** 统计护身符出现次数，仅统计出现过的护身符 */
	effect_counter: IAmuletEffectCounterData[];
	/** 上局和牌手牌（记录牌id） */
	hu_tiles_id: number[];
}

interface IAmuletSkillData extends IProto {
	id: number;
	level: number;
}

