	round: IAmuletGameRoundData;
	effect: IAmuletGameEffectData;
	game: IAmuletGameInfoData;
	stage: number;
	shop: IAmuletShopData;
	record: IAmuletGameRecordData;
	/** 本局游戏是否已经结束 */
	ended: boolean;
